prompt --application/shared_components/navigation/lists/custom_menus
begin
wwv_flow_api.create_list(
 p_id=>wwv_flow_api.id(6355433938640353)
,p_name=>'Custom Menus'
,p_list_type=>'SQL_QUERY'
,p_list_query=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT LEVEL,',
' menu_desc label,',
' DECODE(page_no, NULL, NULL, (''f?p=&APP_ID.:''||page_no||'':&SESSION.::&DEBUG.::::'')) target,',
' null is_current,',
' nvl(MENU_ICON, ''fa-arrow-circle-right'') image,',
' NULL',
' FROM (select id, menu_desc, page_no, menu_icon, par_menu_id, seq_no,menu_home ',
' from menus',
' where status = ''Y''',
' and id in (select c001 menu_id from apex_collections',
'where collection_name = ''MENU_LIST'')',
'union all',
'select id, menu_desc, page_no, menu_icon, par_menu_id, seq_no,menu_home ',
'from menus',
'where status = ''Y''',
'and id in ( select distinct par_menu_id from menus where id in (select c001 menu_id from apex_collections',
'where collection_name = ''MENU_LIST''))  ',
' ) ',
' START WITH par_menu_id IS NULL',
' CONNECT BY par_menu_id = PRIOR id',
' ORDER SIBLINGS BY menu_home desc, seq_no ASC'))
,p_list_status=>'PUBLIC'
);
